import React, {useEffect, useState} from 'react';
import './components.css'
import FontAwesomeIcon from '@fortawesome/free-regular-svg-icons'


const Table = ({ data }) => {
  //Table data, list of items
  const [topTableData, setTopTableData] = useState([]);
  const [bottomTableData, setBottomTableData] = useState([]);


  useEffect(() => {
    // load data here and set it using setData
    async function fetchData() {
      const response = await fetch('http://localhost:3001/todos/');
      const data = await response.json();
      setTopTableData(data);
    }

    fetchData();
  }, []);


  // Handling the delete button
  const handleDelete = (index) => {
    const newTopTableData = [...topTableData];
    newTopTableData.splice(index, 1);
    setTopTableData(newTopTableData);
  }


  // Handling Checkbox / Complete Task
  const handleCheckBox = (index) => {
    console.log(index)
    const newTopTableData = [...topTableData];
    const [row] = newTopTableData.splice(index, 1);
    setTopTableData(newTopTableData);
    setBottomTableData([...bottomTableData, row]);
  }

  return (
    <div>
    <table>
    <thead>
      {<tr>
  <th></th>
  <th>title</th>
  <th>description</th>
  <th>completed</th>
  <th>created</th>
</tr>}
    </thead>
    <tbody>
      {topTableData.map((row, index) => (
        <tr key={row._id}>
        <td><FontAwesomeIcon icon="fa-sharp fa-solid fa-trash-can" /><button onClick={() => handleDelete(index)}>Delete</button></td>
        <td>{row.title}</td>
        <td>{row.description}</td>
        <td><input
              type="checkbox" 
              checked={false}
              onChange={() => handleCheckBox(index)}/>
              </td>
        <td>{row.createdAt}</td>
      </tr>
      ))}
    </tbody>
  </table>

  <hr />
  <table>
  <thead>
          <tr>
            <th>title</th>
            <th>description</th>
            <th>completed</th>
          </tr>
        </thead>
        <tbody>
          {bottomTableData.map((row) => (
            <tr key={row._id}>
              <td>{row.title}</td>
              <td>{row.description}</td>
              <td><input
                    type="checkbox" 
                    checked={true} 
                    disabled/>
              </td>
            </tr>
          ))}
        </tbody>



  </table>




  </div>
  );
};

export default Table;
