import React, {useEffect, useState} from 'react';
import './components.css'


const Table = ({ data }) => {
  //Table data, list of items
  const [topTableData, setTopTableData] = useState([]);
  const [bottomTableData, setBottomTableData] = useState([]);


  useEffect(() => {
    // load data here and set it using setData
    async function fetchData() {
      const response = await fetch('http://localhost:3001/todos/');
      const data = await response.json();
      setTopTableData(data);
    }

    fetchData();
  }, []);




  // Handling Checkbox / Complete Task
  const handleCheckBox = (index) => {

    const newTopTableData = [...topTableData];
    const [row] = newTopTableData.splice(index, 1);
    setTopTableData(newTopTableData);
    setBottomTableData([...bottomTableData, row]);

    // if(e.target.checked){
    //   console.log(e.target.checked)
    //   setCompletedData([...tableData, row]);
    //   setTableData(tableData.filter((item) => item !== row));
    // } else {
    //   setTableData([...tableData, row]);
    //   setCompletedData(completedData.filter((item) => item !== row))
    // }

  }

  return (
    <div>
    <table>
    <thead>
      {<tr>
  <th></th>
  <th>title</th>
  <th>description</th>
  <th>completed</th>
  <th>created</th>
</tr>}
    </thead>
    <tbody>
      {topTableData.map((row, index) => (
        <tr key={row._id}>
        <td><button>Delete</button></td>
        <td>{row.title}</td>
        <td>{row.description}</td>
        <td><input
              type="checkbox" 
              checked={false}
              onChange={() => handleCheckBox(index)}/>
              </td>
        <td>{row.createdAt}</td>
      </tr>
      ))}
    </tbody>
  </table>

  <hr />
  <table>
  <thead>
          <tr>
            <th>title</th>
            <th>description</th>
            <th>completed</th>
          </tr>
        </thead>
        <tbody>
          {bottomTableData.map((row) => (
            <tr key={row._id}>
              <td>{row.title}</td>
              <td>{row.description}</td>
              <td><input
                    type="checkbox" 
                    checked={true} 
                    disabled/>
              </td>
            </tr>
          ))}
        </tbody>



  </table>




  </div>
  );
};

export default Table;
