import React, {useEffect, useState} from 'react';
import './components.css'


const Table = ({ data }) => {
  //Table data, list of items
  const [tableData, setTableData] = useState(null);
  //List of items added to completed table
  const [completedData, setCompletedData] = useState([]);

  useEffect(() => {
    // load data here and set it using setData
    async function fetchData() {
      const response = await fetch('http://localhost:3001/todos/');
      const data = await response.json();
      setTableData(data);
    }

    fetchData();
  }, []);

  if (!tableData) {
    return <div>Loading...</div>;
  }


  // Handling Checkbox / Complete Task
  const handleCheckBox = (e, row) => {

    if(e.target.checked){
      console.log(row)
      setCompletedData([...tableData, row]);
      console.log([row])
      setTableData(tableData.filter((item) => item !== row));
    } else {
      setTableData([...tableData, row]);
      setCompletedData(completedData.filter((item) => item !== row))
      console.log(row)
    }
  }

  return (
    <div>
    <table>
    <thead>
      {<tr>
  <th></th>
  <th>title</th>
  <th>description</th>
  <th>completed</th>
  <th>created</th>
</tr>}
    </thead>
    <tbody>
      {tableData.map((row) => (
        <tr>
        <td><button>Delete</button></td>
        <td>{row.title}</td>
        <td>{row.description}</td>
        <td><input
              type="checkbox" 
              checked={false} 
              onChange={(e) => handleCheckBox(e, row)}/>
              </td>
        <td>{row.createdAt}</td>
      </tr>
      ))}
    </tbody>
  </table>

  <hr />
  <table>
  <thead>
          <tr key={row._id}>
            <th>title</th>
            <th>description</th>
            <th>completed</th>
          </tr>
        </thead>
        <tbody>
          {tableData.map((row) => (
            <tr>
              <td>{row.title}</td>
              <td>{row.description}</td>
              <td><input
                    type="checkbox" 
                    checked={true} 
                    onChange={(e) => handleCheckBox(e, row)}/>
              </td>
            </tr>
          ))}
        </tbody>



  </table>




  </div>
  );
};

export default Table;
